mapper.sql:
FROM `USER_TENANT` A LEFT JOIN `USER` B ON A.USER_ID = B.ID AND B.IS_DELETED = FALSE LEFT JOIN `DEPT` C ON B.DEPT_ID = C.ID AND C.IS_DELETED = FALSE LEFT JOIN POST D ON B.POST_ID = D.ID AND D.IS_DELETED = FALSE
A.TENANT_ID = #{query.id}
AND B.USERNAME LIKE CONCAT('%', #{query.username}, '%')
AND B.NICKNAME LIKE CONCAT('%', #{query.nickname}, '%')
AND C.ID = #{query.deptId}
AND D.ID = #{query.postId}
AND A.IS_ADMIN = #{query.isAdmin}
